import React, { useState } from "react";
import { Link } from "react-router-dom";
import { FaUserCircle, FaShoppingCart } from "react-icons/fa";
import "./Navbar.css"; // Import the CSS file

function Navbar() {
  const [showDropdown, setShowDropdown] = useState(false);

  return (
    <nav className="navbar">
      {/* Navigation Links (Center) */}
      <ul className="nav-links">
        <li><Link to="/">Home</Link></li>
        <li><Link to="/about">About</Link></li>
        <li><Link to="/contact">Contact</Link></li>
        
      </ul>

      {/* Right Side: Cart Icon & Profile */}
      <div className="right-section">
        {/* Cart Icon (Next to Profile Icon) */}
        <Link to="/cart" className="cart-icon">
          <FaShoppingCart size={28} />
        </Link>

        {/* Profile Icon with Hover Dropdown */}
        <div 
          className="profile-container"
          onMouseEnter={() => setShowDropdown(true)}
          onMouseLeave={() => setShowDropdown(false)}
        >
          <FaUserCircle size={28} className="profile-icon" />
          
          {showDropdown && (
            <div className="dropdown-menu">
              <Link to="/profile" className="dropdown-item">My Profile</Link>
              <Link to="/login" className="dropdown-item">Login/Register(std)</Link>
              <Link to="/admin-signup" className="dropdown-item">Login/Register(admin)</Link> {/* âœ… Fixed Link */}
              <Link to="/admin-profile" className="dropdown-item">Admin Profile</Link>

            </div>
          )}
        </div>
      </div>
    </nav>
  );
}

export default Navbar;
