import { useNavigate } from "react-router-dom";

const ProductDetails = ({ product }) => {
  const navigate = useNavigate();

  const handleBuyNow = () => {
    navigate("/order", { state: { product } }); // ✅ Send product data to OrderPage
  };

  return (
    <div>
      <h2>{product.name}</h2>
      <p>Price: ${product.price}</p>
      <button onClick={handleBuyNow}>Buy Now</button> {/* ✅ Trigger navigation */}
    </div>
  );
};
