import React, { useState } from "react";
import "../pages/Register.css"; // Import CSS file
const RegisterPage = () => {
  const [name, setName] = useState("");
  const [pnr, setPnr] = useState("");
  const [email, setEmail] = useState("");
  const [year, setYear] = useState("");
  const [department, setDepartment] = useState("");
  const [password, setPassword] = useState("");

  const handleSubmit = async (event) => {
    event.preventDefault();

    if (!name || !pnr || !email || !year || !department || !password) {
      alert("All fields are required!");
      return;
    }

    const userData = { name, pnr, email, year, department, password };

    console.log("Sending Data:", userData);

    try {
      const response = await fetch("http://localhost:5000/api/auth/register", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify(userData),
      });

      const data = await response.json();
      console.log("Response Data:", data);

      if (!response.ok) {
        throw new Error(data.error || "Registration failed!");
      }

      alert("✅ Registration Successful!");
      window.location.href = "/login"; // Redirect to login page
    } catch (error) {
      console.error("Error:", error.message);
      alert(`❌ Error: ${error.message}`);
    }
  };

  return (
    <form onSubmit={handleSubmit}>
      <input type="text" value={name} onChange={(e) => setName(e.target.value)} placeholder="Name" required />
      <input type="text" value={pnr} onChange={(e) => setPnr(e.target.value)} placeholder="PNR" required />
      <input type="email" value={email} onChange={(e) => setEmail(e.target.value)} placeholder="Email" required />
      <input type="text" value={year} onChange={(e) => setYear(e.target.value)} placeholder="Year" required />
      <input type="text" value={department} onChange={(e) => setDepartment(e.target.value)} placeholder="Department" required />
      <input type="password" value={password} onChange={(e) => setPassword(e.target.value)} placeholder="Password" required />
      <button type="submit">Register</button>
    </form>
  );
};

export default RegisterPage;
