import React, { useContext, useState, useEffect } from "react";
import { useParams, useNavigate } from "react-router-dom";  // ✅ Import useNavigate
import { CartContext } from "../context/CartContext";
import axios from "axios";

const ProductDetails = () => {
    const { id } = useParams();
    const navigate = useNavigate(); // ✅ Hook for navigation
    const { addToCart } = useContext(CartContext);
    const [product, setProduct] = useState(null);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);

    useEffect(() => {
        const fetchProduct = async () => {
            try {
                const res = await axios.get(`http://localhost:5000/api/products/${id}`);
                setProduct(res.data);
            } catch (err) {
                setError("Failed to load product details.");
            } finally {
                setLoading(false);
            }
        };
        fetchProduct();
    }, [id]);

    if (loading) return <p>Loading...</p>;
    if (error) return <p>{error}</p>;
    if (!product) return <p>Product not found.</p>;

    const handleAddToCart = () => {
        addToCart(product, 1);
        navigate("/cart"); // ✅ Redirect to cart page after adding to cart
    };

    return (
        <div className="product-details" style={{ textAlign: "center" }}> {/* ✅ Center details */}
            <h2>{product.name}</h2>
            <img 
                src={product.image} 
                alt={product.name} 
                className="product-image" 
                onError={(e) => e.target.src = "/default-image.jpg"}
                style={{ width: "300px", height: "auto" }}
            />
            <p>{product.description}</p>
            <h3>₹{product.price}</h3>

            {/* ✅ Two Buttons: "Add to Cart" & "Buy Now" */}
            <button onClick={() => addToCart(product, 1)} className="add-cart-btn">
                Add to Cart
            </button>
            <button onClick={handleAddToCart} className="buy-now-btn" style={{ marginLeft: "10px" }}>
                Buy Now
            </button>
        </div>
    );
};

export default ProductDetails;
