import React, { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";

const AdminSignup = () => {
    const navigate = useNavigate();

    const [formData, setFormData] = useState({
        name: "",
        email: "",
        phone: "",
        password: "",
        confirmPassword: "",
        image: null,
    });

    const [error, setError] = useState("");
    const [success, setSuccess] = useState("");
    const [imagePreview, setImagePreview] = useState(null);
    const [isSubmitting, setIsSubmitting] = useState(false);

    // Debugging: Watch state updates
    useEffect(() => {
        console.log("Updated formData:", formData);
    }, [formData]);

    const handleChange = (e) => {
        setFormData((prev) => ({
            ...prev,
            [e.target.name]: e.target.value.trim(), // Trim to avoid extra spaces
        }));
    };

    const handleImageChange = (e) => {
        const file = e.target.files[0];
        if (file) {
            if (!file.type.startsWith("image/")) {
                setError("Only image files are allowed!");
                return;
            }

            if (file.size > 2 * 1024 * 1024) {
                setError("Image size should be less than 2MB!");
                return;
            }

            setFormData((prev) => ({ ...prev, image: file }));

            const reader = new FileReader();
            reader.onloadend = () => {
                setImagePreview(reader.result);
            };
            reader.readAsDataURL(file);
        }
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        setError(""); // Clear previous errors
        setSuccess("");

        // Validate required fields
        if (!formData.name || !formData.email || !formData.phone || !formData.password || !formData.confirmPassword) {
            setError("All fields are required!");
            return;
        }

        if (formData.password.length < 6) {
            setError("Password must be at least 6 characters!");
            return;
        }

        if (formData.password !== formData.confirmPassword) {
            setError("Passwords do not match!");
            return;
        }

        setIsSubmitting(true);

        // Create FormData object for file upload
        const formDataToSend = new FormData();
        formDataToSend.append("name", formData.name);
        formDataToSend.append("email", formData.email);
        formDataToSend.append("phone", formData.phone);
        formDataToSend.append("password", formData.password);
        if (formData.image) {
            formDataToSend.append("image", formData.image);
        }

        try {
            const response = await fetch("http://localhost:5000/api/auth/register", {
                method: "POST",
                body: formDataToSend, // Use FormData for file uploads
            });

            const data = await response.json();
            console.log("API Response:", data);

            if (!response.ok) {
                setError(data.error || "Something went wrong!");
                return;
            }

            setSuccess("Registration successful!");
            setTimeout(() => navigate("/login"), 2000);
        } catch (error) {
            setError("Error: " + error.message);
        } finally {
            setIsSubmitting(false);
        }
    };

    return (
        <div style={styles.container}>
            <h2>Admin Signup</h2>
            {error && <p style={styles.error}>{error}</p>}
            {success && <p style={styles.success}>{success}</p>}

            <form onSubmit={handleSubmit} style={styles.form}>
                <label>Full Name</label>
                <input type="text" name="name" value={formData.name} onChange={handleChange} required />

                <label>Email</label>
                <input type="email" name="email" value={formData.email} onChange={handleChange} required />

                <label>Phone Number</label>
                <input type="tel" name="phone" value={formData.phone} onChange={handleChange} required />

                <label>Password</label>
                <input type="password" name="password" value={formData.password} onChange={handleChange} required />

                <label>Confirm Password</label>
                <input type="password" name="confirmPassword" value={formData.confirmPassword} onChange={handleChange} required />

                <label>Upload Profile Picture</label>
                <input type="file" accept="image/*" onChange={handleImageChange} />

                {imagePreview && <img src={imagePreview} alt="Profile Preview" style={styles.imagePreview} />}

                <button type="submit" disabled={isSubmitting}>
                    {isSubmitting ? "Registering..." : "Signup"}
                </button>
            </form>

            <button style={styles.loginButton} onClick={() => navigate("/StdLogin")}>
                Login
            </button>
        </div>
    );
};

const styles = {
    container: {
        width: "320px",
        margin: "50px auto",
        padding: "20px",
        border: "1px solid #ccc",
        borderRadius: "8px",
        textAlign: "center",
        backgroundColor: "#f9f9f9",
        boxShadow: "0px 0px 10px rgba(0, 0, 0, 0.1)",
    },
    form: {
        display: "flex",
        flexDirection: "column",
        gap: "10px",
    },
    error: {
        color: "red",
        fontSize: "14px",
    },
    success: {
        color: "green",
        fontSize: "14px",
    },
    imagePreview: {
        width: "100px",
        height: "100px",
        borderRadius: "50%",
        objectFit: "cover",
        marginTop: "10px",
    },
    loginButton: {
        marginTop: "15px",
        padding: "10px",
        backgroundColor: "#007bff",
        color: "white",
        border: "none",
        borderRadius: "5px",
        cursor: "pointer",
        width: "100%",
    },
};

export default AdminSignup;
