import React from "react";
import { useLocation } from "react-router-dom";

const AdminProfile = () => {
    const location = useLocation();
    const { name, imagePreview } = location.state || {};

    return (
        <div style={styles.container}>
            <div style={styles.sidebar}>
                {imagePreview ? (
                    <img src={imagePreview} alt="Admin" style={styles.profileImage} />
                ) : (
                    <div style={styles.placeholderImage}>No Image</div>
                )}
                <h3>{name || "Admin"}</h3>
            </div>
            <div style={styles.content}>
                <h2>Welcome, {name || "Admin"}!</h2>
                <p>This is your admin dashboard.</p>
            </div>
        </div>
    );
};

const styles = {
    container: {
        display: "flex",
        height: "100vh",
    },
    sidebar: {
        width: "250px",
        backgroundColor: "#282c34",
        color: "white",
        display: "flex",
        flexDirection: "column",
        alignItems: "center",
        padding: "20px",
    },
    profileImage: {
        width: "100px",
        height: "100px",
        borderRadius: "50%",
        objectFit: "cover",
        marginBottom: "10px",
    },
    placeholderImage: {
        width: "100px",
        height: "100px",
        borderRadius: "50%",
        backgroundColor: "gray",
        display: "flex",
        alignItems: "center",
        justifyContent: "center",
        color: "white",
        marginBottom: "10px",
    },
    content: {
        flex: 1,
        padding: "20px",
    },
};

export default AdminProfile;
