import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import axios from "axios";
import "./Register.css"; // Import CSS for styling

const Register = () => {
  const navigate = useNavigate();

  const [formData, setFormData] = useState({
    name: "",
    pnr: "",
    email: "",
    year: "",
    department: "",
    password: "",
    profileImage: null, // For image upload
  });

  const [error, setError] = useState("");
  const [success, setSuccess] = useState("");
  const [previewImage, setPreviewImage] = useState(null); // To show image preview

  // Handle input change
  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  // Handle image upload
  const handleImageChange = (e) => {
    const file = e.target.files[0];
    if (file) {
      setFormData({ ...formData, profileImage: file });
      setPreviewImage(URL.createObjectURL(file)); // Show image preview
    }
  };

  // Handle form submission
  const handleSubmit = async (e) => {
    e.preventDefault();
    setError("");
    setSuccess("");

    const data = new FormData();
    data.append("name", formData.name);
    data.append("pnr", formData.pnr);
    data.append("email", formData.email);
    data.append("year", formData.year);
    data.append("department", formData.department);
    data.append("password", formData.password);
    if (formData.profileImage) {
      data.append("profileImage", formData.profileImage); // Append the image
    }

    try {
      const res = await axios.post("http://localhost:5000/api/auth/register", data, {
        headers: { "Content-Type": "multipart/form-data" }, // Required for file upload
      });

      setSuccess("Registration successful! Redirecting to login...");
      setTimeout(() => navigate("/login"), 2000);
    } catch (err) {
      setError(err.response?.data?.message || "Something went wrong!");
    }
  };

  return (
    <div className="register-container">
      <div className="register-card">
        <h2>Register</h2>
        {error && <p className="error-message">{error}</p>}
        {success && <p className="success-message">{success}</p>}

        <form onSubmit={handleSubmit}>
          {/* Name */}
          <div className="input-group">
            <label>Name</label>
            <input type="text" name="name" placeholder="Enter your name" value={formData.name} onChange={handleChange} required />
          </div>

          {/* PNR Number */}
          <div className="input-group">
            <label>PNR Number</label>
            <input type="text" name="pnr" placeholder="Enter your PNR number" value={formData.pnr} onChange={handleChange} required />
          </div>

          {/* Email */}
          <div className="input-group">
            <label>Email</label>
            <input type="email" name="email" placeholder="Enter your email" value={formData.email} onChange={handleChange} required />
          </div>

          {/* Year of Studying */}
          <div className="input-group">
            <label>Year of Studying</label>
            <select name="year" value={formData.year} onChange={handleChange} required>
              <option value="">Select Year</option>
              <option value="1st Year">1st Year</option>
              <option value="2nd Year">2nd Year</option>
              <option value="3rd Year">3rd Year</option>
              <option value="4th Year">4th Year</option>
            </select>
          </div>

          {/* Department */}
          <div className="input-group">
            <label>Department</label>
            <input type="text" name="department" placeholder="Enter your department" value={formData.department} onChange={handleChange} required />
          </div>

          {/* Password */}
          <div className="input-group">
            <label>Password</label>
            <input type="password" name="password" placeholder="Enter your password" value={formData.password} onChange={handleChange} required />
          </div>

          {/* Image Upload */}
          <div className="input-group">
            <label>Upload Profile Image</label>
            <input type="file" accept="image/*" onChange={handleImageChange} required />
            {previewImage && <img src={previewImage} alt="Profile Preview" className="image-preview" />}
          </div>

          {/* Submit Button */}
          <button type="submit" className="register-btn">Register</button>
        </form>

        {/* Login Link */}
        <p className="login-link">Already have an account? <span onClick={() => navigate("/login")}>Login</span></p>
      </div>
    </div>
  );
};

export default Register;
