import React from "react";
import { BrowserRouter as Router, Routes, Route } from "react-router-dom";
import Navbar from "./components/Navbar";
import Home from "./pages/Home";
import Login from "./pages/Login";
import Register from "./pages/Register";
import About from "./pages/About";
import Contact from "./pages/Contact";
import ProductDetails from "./pages/ProductDetails";
import CartPage from "./pages/CartPage"; // ✅ Import Cart Page
import { CartProvider } from "./context/CartContext"; // ✅ Import Cart Context

function App() {
  return (
    <Router>
      <CartProvider> {/* ✅ Wrap with CartProvider to enable cart functionality */}
        <Navbar />
        <Routes>
          <Route path="/" element={<Home />} />
          <Route path="/about" element={<About />} />
          <Route path="/contact" element={<Contact />} />
          <Route path="/login" element={<Login />} />
          <Route path="/register" element={<Register />} />
          <Route path="/product/:id" element={<ProductDetails />} />
          <Route path="/cart" element={<CartPage />} /> {/* ✅ Added Cart Page Route */}
        </Routes>
      </CartProvider>
    </Router>
  );
}

export default App;
