import React, { useState } from "react";
import { useParams } from "react-router-dom";
import { useCart } from "../pages/context/CartContext"; // âœ… Import useCart
import axios from "axios";
import "./ProductDetails.css"; // Add styles if needed

const ProductDetails = () => {
    const { id } = useParams();
    const { addToCart } = useCart(); // âœ… Access CartContext
    const [product, setProduct] = useState(null);
    const [quantity, setQuantity] = useState(1);

    React.useEffect(() => {
        const fetchProduct = async () => {
            try {
                const res = await axios.get(`http://localhost:5000/api/products/${id}`);
                setProduct(res.data);
            } catch (error) {
                console.error("Error fetching product details:", error);
            }
        };

        fetchProduct();
    }, [id]);

    if (!product) return <p>Loading product details...</p>;

    return (
        <div className="product-details">
            <img src={product.image} alt={product.name} />
            <h2>{product.name}</h2>
            <p>{product.description}</p>
            <h3>â‚¹{product.price}</h3>

            <div>
                <label>Quantity: </label>
                <input
                    type="number"
                    value={quantity}
                    min="1"
                    onChange={(e) => setQuantity(parseInt(e.target.value))}
                />
            </div>

            <button onClick={() => addToCart(product, quantity)}>ðŸ›’ Add to Cart</button>
            <button>âš¡ Buy Now</button>
        </div>
    );
};

export default ProductDetails;
